local function decodeImage(b64, filename)
    if not (b64 and love and love.data and love.filesystem and love.graphics) then
        return nil
    end
    local okData, decoded = pcall(love.data.decode, "string", "base64", b64)
    if not okData then
        return nil
    end
    local okFile, fileData = pcall(love.filesystem.newFileData, decoded, filename)
    if not okFile then
        return nil
    end
    local okImage, image = pcall(love.graphics.newImage, fileData)
    if not okImage then
        return nil
    end
    return image
end

local function decodeImageData(b64, filename)
    if not (b64 and love and love.data and love.filesystem and love.image) then
        return nil
    end
    local okData, decoded = pcall(love.data.decode, "string", "base64", b64)
    if not okData then
        return nil
    end
    local okFile, fileData = pcall(love.filesystem.newFileData, decoded, filename)
    if not okFile then
        return nil
    end
    local okImageData, imageData = pcall(love.image.newImageData, fileData)
    if not okImageData then
        return nil
    end
    return imageData
end

function EmbeddedAssets.buildCursorImageData()
    return decodeImageData(EmbeddedAssets.cursorGauntlet, "cursorGauntlet.png")
end

function EmbeddedAssets.buildLightImages()
    local images = {}
    for index, b64 in ipairs(EmbeddedAssets.lightSprites) do
        local image = decodeImage(b64, string.format("light_%02d.png", index))
        if image then
            image:setFilter("linear", "linear")
            table.insert(images, image)
        end
    end
    return images
end

return EmbeddedAssets
